import React,{useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import AccountCircle from '@material-ui/icons/AccountCircle';

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
  grid:{
    marginLeft:'5px'
  }
}));

export default function LoginForm(props) {
  const {username,setUsername,getFormValue} =props;
  const [fullName, setFullName] = useState({firstName:'',lastName:''})
  const classes = useStyles();
  const setName=e=>{
     setFullName(pre=>({
       ...pre,
       [name]:value
     })
     )
     //setUsername(e)
  }

  return (
    <div>
      <div className={classes.margin}>
        <Grid container spacing={1} alignItems="flex-end">
          <Grid item>
            <AccountCircle />
          </Grid>
          <Grid item className={classes.grid}>
            <TextField id="input-with-icon-grid" name="firstName" label="First name" onChange={setName}/>
          </Grid>

          <Grid item>
            <TextField id="input-with-icon-grid" name="lastName" label="Last name" onChange={setName} />
          </Grid>

        </Grid>
      </div>
    </div>
  );
}
